  const [count, setCount] = useState(12);
  return (
    <Comments>
      <img
        src="/images/avatars/image-juliusomo.webp"
        style={{ width: "30px", height: "30px", margin: "12px" }}
      />
      <CommentInput></CommentInput>
      <ReplayButton>Reply</ReplayButton>
    </Comments>
  );
};

export default CommentReplies;
