import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useQuery, useMutation } from "react-query";
import { getComments } from "../services/comments";
Comm

const Container = styled.div`
  margin: 0 auto;
  padding: 1rem;
  width: 800px;
`;

const Comments = styled.ul`
  list-style: none;
  margin: 10px;
  padding: 0px;
  display: flex;
  background: #ffffff;
  min-height: 130px;
`;

const CountSection = styled.div`
  display: flex;
  align-items: center;
  background: #f5f6fa;
  margin: 15px;
  flex-direction: column;
  width: 70px;
  border-radius: 8px;
  height: 80px;
`;

const Sign = styled.span`
  color: #696cae;
  font-size: 18px;
  font-weight: 600;
`;

const Comment = styled.li`
  margin-bottom: 5px;
  padding: 15px;
  padding-left: 0;
  color: #8e9198;
`;

const CommentHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const CommentAuthor = styled.span``;

const ReplaySection = styled.span`
color: #7c7ab9;
font-size: 13px;
.icon{
    padding-right: 3px
}
`;
const CommentContent = styled.p`
  margin-top: 0.5rem;
`;

const CommentActions = styled.div`
  display: flex;
  align-items: center;
`;

const CommentAction = styled.button`
  border: none;
  background: none;
  cursor: pointer;
  margin-left: 1rem;

  &:hover {
    text-decoration: underline;
  }
`;

const CommentsScreen = () => {
  const { data, isLoading } = useQuery("comments", getComments);
  const [newComment, setNewComment] = useState("");
  const [editedComment, setEditedComment] = useState(null);
  const [showDeleteConfirmationModal, setShowDeleteConfirmationModal] =
    useState(false);
  const [count, setCount] = useState(12);
  return (
    <>
      <Container>
        {data?.comments.map((comment) => (
          <Comments>
            <CountSection>
              <Sign>+</Sign>
              {count}
              <Sign>-</Sign>
            </CountSection>
            <Comment>
              <CommentHeader>
                <CommentAuthor>
                  <avatar>
                    <img src={comment.user.image.png} />
                  </avatar>
                  <span style={{ color: "#505860", fontWeight: "bold" }}>
                    {" "}
                    {comment.user.username}{" "}
                  </span >
                  <span style={{ paddingLeft:"5px"}}> {comment.createdAt}</span>
                </CommentAuthor>
                <ReplaySection><MemoIconReply className="icon"/>Reply</ReplaySection>
              </CommentHeader>
              <CommentContent>{comment.content}</CommentContent>
            </Comment>
          </Comments>
        ))}
      </Container>
    </>
  );
};

export default CommentsScreen;
