import React, { useState } from 'react';


function CommentDeleteDialogBox(setShowDialog,props) {


  const handleDelete = () => {
    // Perform delete action here
    console.log('Comment deleted!');
    setShowDialog(false);
  };

  const handleCancel = () => {
    setShowDialog(false);
  };

  return (
    <div>
      
        <div className="delete-message-dialog">
          <h2>{props.heading}</h2>
          <p>{props.message}</p>
          <div className="delete-message-dialog-buttons">
            <button onClick={handleCancel}>NO,CANCEL</button>
            <button onClick={handleDelete}>YES, DELETE</button>
          </div>
        </div>
      
    </div>
  );
}

export default CommentDeleteDialogBox;
