import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { CommentInput, Comments, ReplayButton } from "../../styles/CommentStyles";
import axios from "axios";
import { useMutation } from "react-query";


const CommentReplies = ({setShowReply,onAddReply,postId }) => {
  const [username, setUsername] = useState("");
  const [content, setContent] = useState("");
  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handleContentChange = (event) => {
    setContent(event.target.value);
  };

  const [mutateAddComment] = useMutation(
    async () => {
      const response = await axios.post(
        `url/posts/${postId}/comments`,
        {
          username,
          content,
        }
      );
      return response.data;
    },
    {
      onSuccess: () => {
        queryCache.invalidateQueries("comments");
      },
    }
  );
  return (
    <Comments>
      <img
        src="/images/avatars/image-juliusomo.webp"
        style={{ width: "30px", height: "30px", margin: "12px" }}
      />
      <CommentInput/>
      <ReplayButton onClick={() => {onAddReply();setShowReply(false)}}>Reply</ReplayButton>
    </Comments>
  );
};

export default CommentReplies;
