import styled from "styled-components";

export const Comments = styled.ul`
  list-style: none;
  padding-left: 8px;
  margin: 0px;
  padding: 0px;
  display: flex;
  background: #ffffff;
  min-height: 130px;
  border-radius: 8px;
  width: 100%;

`;

export const ReplayButton = styled.button`
  background: #5358b6;
  margin: 12px;
  border-radius: 8px;
  height: 30px;
  width: 80px;
  border: none;
  color: #fafdff;
`;

export const CommentInput = styled.textarea`
  font-size: 14px;
  font-weight: 400;
  width: 100%;
  height: 100px;
  margin: 12px 0px;
  border-radius: 8px;
`;

const CommentForm = styled.form`
  margin-top: 1rem;
`;

const CommentTextarea = styled.textarea`
  width: 100%;
  padding: 0.5rem;
  border: 1px solid #ccc;
  border-radius: 4px;
  resize: vertical;
`;

const CommentButton = styled.button`
  display: block;
  margin-top: 0.5rem;
  border: none;
  background-color: #0077cc;
  color: #fff;
  padding: 0.5rem 1rem;
  border-radius: 4px;
  cursor: pointer;

  &:hover {
    background-color: #005fa3;
  }

  &:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }
`;

const DeleteConfirmationModal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: rgba(0, 0, 0, 0.5);
`;

const ModalContent = styled.div`
  background-color: #fff;
  padding: 1rem;
  border-radius: 4px;
  text-align: center;
`;

const ModalHeader = styled.h2`
  margin-top: 0;
`;

const ModalText = styled.p`
  margin-bottom: 1rem;
`;

const ModalButton = styled.button`
  margin-right: 0.5rem;
`;
