import React, { useState, useEffect } from "react";
import styled from "styled-components";
import { useQuery, useMutation } from "react-query";
import { getComments } from "../services/comments";
import CommentCard from "../components/Comment/CommentCard";
import CommentReplies from "../components/Comment/CommentReplies";

const Container = styled.section`
  margin: 0 auto;
  padding: 1rem;
  width: 800px;
`;

const SubContainer = styled.div`
  margin: 0px auto;
  padding-left: 20px;
  display: flex;
  border-left: 2px solid #eff0f4;
  margin-left: 40px;
`;

const CommentsScreen = () => {
  const { data, isLoading } = useQuery("comments", getComments);
  const [newComment, setNewComment] = useState("");
  const [editedComment, setEditedComment] = useState(null);
  const [showDeleteConfirmationModal, setShowDeleteConfirmationModal] =
    useState(false);
 const currentUser = data?.currentUser?.username;
  return (
    <Container>
      {console.log(currentUser)}
      {data?.comments.map((comment) => (
        <>
          <CommentCard data={comment} currentUser={currentUser} />
          <CommentReplies />
          {comment.replies?.map((reply) => (
            <SubContainer>
              <CommentCard data={reply} currentUser={currentUser} />
            </SubContainer>
          ))}
        </>
      ))}
    </Container>
  );
};

export default CommentsScreen;
