import React, { useState } from 'react';


function CommentDeleteDialogBox(props) {
  const [showDialog, setShowDialog] = useState(false);

  const handleDelete = () => {
    // Perform delete action here
    console.log('Comment deleted!');
    setShowDialog(false);
  };

  const handleCancel = () => {
    setShowDialog(false);
  };

  return (
    <div>
      <button onClick={() => setShowDialog(true)}>Delete Message</button>
      {showDialog && (
        <div className="delete-message-dialog">
          <h2>{props.heading}</h2>
          <p>{props.message}</p>
          <div className="delete-message-dialog-buttons">
            <button onClick={handleCancel}>Cancel</button>
            <button onClick={handleDelete}>Delete</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default CommentDeleteDialogBox;
