import React, { useState, useEffect } from "react";
import styled from "styled-components";
import MemoIconReply from "../../images/IconReply";

const CountSection = styled.div`
  display: flex;
  align-items: center;
  background: #f5f6fa;
  margin: 15px;
  flex-direction: column;
  width: 70px;
  border-radius: 8px;
  height: 80px;
`;

const Sign = styled.span`
  color: #696cae;
  font-size: 18px;
  font-weight: 600;
`;

const Comment = styled.li`
  margin-bottom: 5px;
  padding: 15px;
  padding-left: 0;
  color: #8e9198;
`;

const CommentHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
`;

const CommentAuthor = styled.span``;

const ReplaySection = styled.span`
  color: #7c7ab9;
  font-size: 13px;
  cursor: pointer;
  .icon {
    padding-right: 3px;
  }
`;
const CommentContent = styled.p`
  margin-top: 0.5rem;
`;

const CommentReplies = ({data}) => {
  const [count, setCount] = useState(12);
  return (
    <>
      <Comment>
        <CommentHeader>

            <avatar>
              <img src={data.user?.image.png} />
            </avatar>
            <span style={{ color: "#505860", fontWeight: "bold" }}>
              {" "}
              {data.user?.username}{" "}
            </span>
            <span style={{ paddingLeft: "5px" }}> {data.createdAt}</span>

          <ReplayButton>
            Reply
          </ReplayButton>
        </CommentHeader>
      </Comment>
    </>
  );
};

export default CommentReplies;
