import swal from 'sweetalert';

const showConfirmationBox = (title, message, confirmLabel, cancelLabel) => {
  swal({
    title: title,
    text: message,
    icon: "warning",
    buttons: {
      cancel: {
        text: cancelLabel,
        value: false,
        visible: true,
        className: "",
        closeModal: true,
      },
      confirm: {
        text: confirmLabel,
        value: true,
        visible: true,
        className: "",
        closeModal: true
      }
    },
    dangerMode: true,
  })
  .then((confirmed) => {
    if (confirmed) {
      // User clicked the confirm button
      swal({
        title: "File deleted!",
        icon: "success",
      });
    } else {
      // User clicked the cancel button
      swal({
        title: "File not deleted!",
        icon: "error",
      });
    }
  });
}

function MyComponent() {
  const handleDeleteButtonClick = () => {
    showConfirmationBox("Delete File", "Are you sure you want to delete this file?", "Delete", "Cancel");
  }

  return (
    <div>
      <button onClick={handleDeleteButtonClick}>Delete File</button>
    </div>
  );
}
