import React, { useState } from "react";
import styled from "styled-components";
const Wrapper = styled.div`
  display: flex;
  align-items: center;
  background: #f5f6fa;
  margin: 15px;
  flex-direction: column;
  width: 50px;
  border-radius: 8px;
  height: 80px;
`;

function CommentDeleteDialogBox(setShowDialog, props) {
  const handleDelete = () => {
    // Perform delete action here
    console.log("Comment deleted!");
    setShowDialog(false);
  };

  const handleCancel = () => {
    setShowDialog(false);
  };

  return (
    <Wrapper>
      <div className="delete-message-dialog">
        <h2>{props.heading}</h2>
        <p>{props.message}</p>
        <div className="delete-message-dialog-buttons">
          <button onClick={handleCancel}>NO,CANCEL</button>
          <button onClick={handleDelete}>YES, DELETE</button>
        </div>
      </div>
    </Wrapper>
  );
}

export default CommentDeleteDialogBox;
